function BasketItem(props) {
    const { mainId, displayName, price, quantity, removeFromBasket, incQuantity, decQuantity } = props;

    return (
        <ul className="collection">
            <li className="collection-item">
                {displayName} <i className="material-icons basket-quantity deep-purple accent-1" onClick={() => decQuantity(mainId)}>remove</i> x {quantity}<i className="material-icons basket-quantity deep-purple accent-1" onClick={() => incQuantity(mainId)}>add</i> = {price * quantity} V-Bucks
                <span className="secondary-content" onClick={() => removeFromBasket(mainId)}>
                    <i className="material-icons basket-delete deep-purple accent-1">clear</i>
                </span>
            </li>
        </ul>
    );
}
export { BasketItem };
