import { BasketItem } from "./basketItem";

function BasketList(props) {
    const { order = [], handleBasketShow, removeFromBasket, incQuantity, decQuantity } = props;
    const totalPrice = order.reduce((sum, el) => sum + (el.price.finalPrice * el.quantity), 0);

    return (
        <>
            <div className="basket-overlay" onClick={handleBasketShow}></div>
            <div className="basket-modal">
                <div className="basket-header white-text">
                    <h5>Корзина</h5>
                    <i className="material-icons basket-clear" 
                        onClick={handleBasketShow}>
                        clear
                    </i>
                </div>

                <div className="basket-body">
                    {order.length ? order.map((item) => (
                        <BasketItem 
                            key={item.mainId} 
                            {...item}
                            removeFromBasket={removeFromBasket}
                            incQuantity={incQuantity}
                            decQuantity={decQuantity}
                            price={item.price.finalPrice}
                        />
                    )) : <div className="center">Корзина пуста</div>}
                </div>

                <div className="basket-footer white-text">
                    Общая стоимость: {totalPrice} V-Bucks
                </div>
            </div>
        </>
    );
}
export { BasketList };
