// BasketItem.jsx
function BasketItem(props) {
    const { mainId, displayName, price, quantity, removeFromBasket, incQuantity, decQuantity } = props;

    return (
        <div className="basket-item">
            <div className="item-info">
                <span>{displayName}</span>
                <div className="quantity-controls">
                    <i className="material-icons" onClick={() => decQuantity(mainId)}>remove</i>
                    <span>{quantity}</span>
                    <i className="material-icons" onClick={() => incQuantity(mainId)}>add</i>
                </div>
                <span>{price * quantity} V-Bucks</span>
            </div>
            <i className="material-icons delete-icon" onClick={() => removeFromBasket(mainId)}>clear</i>
        </div>
    );
}
export { BasketItem };
