import { BasketItem } from "./basketItem";

function BasketList(props) {
    const { order = [], handleBasketShow = Function.prototype, removeFromBasket = Function.prototype, incQuantity = Function.prototype, decQuantity = Function.prototype, } = props;
    const totalPrice = order.reduce((sum, el) => {
        return sum + (el.price.finalPrice * el.quantity);
    }, 0);
    return (
        <>
            <div className="basket-overlay" onClick={handleBasketShow}></div>
            <ul className="collection basket-container">
                <li className="collection-item active deep-purple accent-1 basket-header">
                    Корзина
                    <i className="material-icons secondary-content basket-clear" 
                        onClick={handleBasketShow}>
                        clear
                    </i>
                </li>
                <div className="basket-items-wrapper">
                    {order.length ? order.map((item) => (
                        <BasketItem 
                            key={item.mainId} 
                            removeFromBasket={removeFromBasket} 
                            incQuantity={incQuantity} 
                            decQuantity={decQuantity} 
                            {...item} 
                            price={item.price.finalPrice} 
                        />
                    )) : <li className="collection-item">Корзина пуста</li>}
                </div>
                <li className="collection-item active white-text deep-purple accent-1 basket-footer">
                    Общая стоимость заказа: {totalPrice} V-Bucks.
                </li>
            </ul>
        </>
    );
}
export { BasketList };
