import { BasketItem } from "./basketItem";


function BasketList(props) {
    // ...остальной код

    return (
        <>
            <div className="basket-overlay" onClick={handleBasketShow}></div>
            <ul className="collection">
                <li className="collection-item active">
                    Корзина
                    <i className="material-icons secondary-content basket-clear white-text" 
                        onClick={handleBasketShow}>
                        clear
                    </i>
                </li>

                {/* Убрать div и использовать li напрямую */}
                {order.length ? order.map((item) => (
                    <BasketItem 
                        key={item.mainId} 
                        removeFromBasket={removeFromBasket} 
                        incQuantity={incQuantity} 
                        decQuantity={decQuantity} 
                        {...item} 
                        price={item.price.finalPrice} 
                    />
                )) : <li className="collection-item">Корзина пуста</li>}

                <li className="collection-item active white-text">
                    Общая стоимость заказа: {totalPrice} V-Bucks.
                </li>
            </ul>
        </>
    );
}
export { BasketList };
